type Cert struct {
	Raw  string
	Cert *x509.Certificate
}

var oidCommonName = asn1.ObjectIdentifier{2, 5, 4, 3}
var oidCountryName = asn1.ObjectIdentifier{2, 5, 4, 6}
var oidOrganizationName = asn1.ObjectIdentifier{2, 5, 4, 10}
var oidOrganizationalUnitName = asn1.ObjectIdentifier{2, 5, 4, 11}

// http://www.alvestrand.no/objectid/2.5.4.html
var oids = map[string]asn1.ObjectIdentifier{
	"commonName":             oidCommonName,
	"countryName":            oidCountryName,
	"organizationName":       oidOrganizationName,
	"organizationalUnitName": oidOrganizationalUnitName,
}

func getOidName(oid asn1.ObjectIdentifier) string {
	for k, v := range oids {
		if oid.Equal(v) {
			return k
		}
	}

	return Unknow
}

func getValueByOid(names []pkix.AttributeTypeAndValue, oid asn1.ObjectIdentifier) string {
	for _, v := range names {
		if oid.Equal(v.Type) {
			return v.Value.(string)
		}
	}

	return Unknow
}

