	Log.Info("Certificate-UnhandledCriticalExtensions %v", c.UnhandledCriticalExtensions)
	Log.Info("Certificate-ExtKeyUsage %v", c.ExtKeyUsage)
	Log.Info("Certificate-UnknownExtKeyUsage %v", c.UnknownExtKeyUsage)
	Log.Info("Certificate-BasicConstraintsValid %v", c.BasicConstraintsValid)
	Log.Info("Certificate-IsCA %v", c.IsCA)
