func main() {
	for key, certs := range vendors {
		for idx, cert := range certs {
			Log.Info("========================================")
			Log.Info("         %s cert:%d", key, idx)
			Log.Info("========================================")

			Len := len(cert.Raw)
			buf := make([]byte, Len/2)
			hex.Decode(buf, []byte(cert.Raw))

			c, err := x509.ParseCertificate(buf)
			if nil != err {
				Log.Info("Parse %s Certificate %d error:%v", key, idx, err)

				os.Exit(1)
			}
			cert.Cert = c

			if idx > 0 {
				err := certs[idx-1].Cert.CheckSignatureFrom(certs[idx].Cert)
				if nil != err {
					Log.Info("CheckSignatureFrom %d:%d error:%v", idx-1, idx, err)

					os.Exit(1)
				} else {
					Log.Info("CheckSignatureFrom %d:%d OK", idx-1, idx)
				}
			}

			show(c)
		}
	}
}
